void print_array(int *arr, int n)
{
    printf("\n=============================\n");
    printf("(Debug only) \n");
    for (int i = 0; i < n; i++)
    {
        printf("%d ", arr[i]);
    }
    printf("\n");
    printf("=============================");
}

void print_array(unsigned int *arr, int n)
{
    printf("\n=============================\n");
    printf("(Debug only) \n");
    for (int i = 0; i < n; i++)
    {
        printf("%u ", arr[i]);
    }
    printf("\n");
    printf("=============================");
}

int get_date_time(char *buffer)
{
    time_t time_raw_format;
    struct tm *ptr_time;

    time(&time_raw_format);
    ptr_time = localtime(&time_raw_format);
    if (strftime(buffer, 50, "%Y_%m_%d", ptr_time) == 0)
    {
        perror("Couldn't prepare formatted string");
        return -1;
    }

    return 1;
}

// High-resolution timer.
double getCurrentTimestamp()
{
#ifdef _WIN32 // Windows
    // Use the high-resolution performance counter.

    static LARGE_INTEGER ticks_per_second = {};
    if (ticks_per_second.QuadPart == 0)
    {
        // First call - get the frequency.
        QueryPerformanceFrequency(&ticks_per_second);
    }

    LARGE_INTEGER counter;
    QueryPerformanceCounter(&counter);

    double seconds = double(counter.QuadPart) / double(ticks_per_second.QuadPart);
    return seconds;
#else // Linux
    timespec a;
    clock_gettime(CLOCK_REALTIME, &a);
    return (double(a.tv_nsec) * 1.0e-9) + double(a.tv_sec);
#endif
}

